import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ContactFormModel } from 'src/app/Models/ContactFormModel';

@Component({
  selector: 'app-contact-form',
  templateUrl: './contact-form.component.html',
  styleUrls: ['./contact-form.component.css']
})
export class ContactFormComponent implements OnInit{

  ContactModel = new ContactFormModel(); //creamos una instancia de nuestro modelo


  public mensajeError: string;
  public mensajePassword: string;
  public passWordCorrecta: boolean; //booleanas para comprobacion de las password
  public passWord2Correcta: boolean; //booleanas para comprobacion de las password
  public nombreCorrecto: boolean;
  public apellidosCorrectos: boolean;
  public telefonoCorrecto: boolean;
  public mailCorrecto: boolean;
  public camposCorrectos: boolean;

  constructor(){}

  ngOnInit(): void {
          /*En el inicio dejamos los mensajes de error sin mostrat */
          this.mensajeError = '';
          this.mensajePassword = '';
  }


    /*CREAMOS NUESTRAS VARIABLES Y NUESTRA VARIABLE DEL MODELO "CONTACTMODEL"*/



    onSubmit(formulario: NgForm) {
      //llamamos al servicio y el método que contiene
      alert('Formulario enviado correctamente');
    }


    /*METODOS PARA VALIDAR CAMPOS OBLIGATORIOS*/
    public validarCamposObligatorios(evento: any): void {

      //Hacemos un switch para que ejecute un codigo en funcion del input que se ha modificado
      switch (evento.target.name) {
        case 'nombre':
          const inputNombre = document.getElementById('nombre');
          if (this.ContactModel.nombre.length  >= 3  && /^[A-Z ]+$/i.test(this.ContactModel.nombre)) {
            inputNombre?.classList.remove('is-invalid');
            this.nombreCorrecto = true;
          } else {
            inputNombre?.classList.add('is-invalid');
            this.nombreCorrecto = false;
          }
          break;

        case 'apellidos':
          const inputApellidos = document.getElementById('apellidos');
          if (this.ContactModel.apellidos.length >= 3 && /^[A-Z ]+$/i.test(this.ContactModel.apellidos)) {
            inputApellidos?.classList.remove('is-invalid');
            this.apellidosCorrectos = true;
          } else {
            inputApellidos?.classList.add('is-invalid');
            this.apellidosCorrectos = false;
          }
          break;

        case 'numeroTelefono':
          const telefonoExpresion = {
              telefono: /^\d{9}$/
              }
          const inputTelefono = document.getElementById('telefono');
          if (telefonoExpresion.telefono.test(evento.target.value)) {
            inputTelefono?.classList.remove('is-invalid');
            this.telefonoCorrecto = true;
          } else {
            inputTelefono?.classList.add('is-invalid');
            this.telefonoCorrecto = false;
          }
          break;

        case 'email':
          const mailExpresionn = {
              email: /\S+@\S+\.\S+/,
              }
          const inputMail = document.getElementById('email');
          if (mailExpresionn.email.test(evento.target.value)) {
            inputMail?.classList.remove('is-invalid');
            this.mailCorrecto = true;
          } else {
            inputMail?.classList.add('is-invalid');
            this.mailCorrecto = false;
          }
          break;
      }
    }

    /*METODO PARA VALIDAR LA CONTRASEÑA*/
    public correctPass(event: any): void {
      if (this.ContactModel.password.length != 6) {
        this.ContactModel.passCorrecta = false;
        this.passWordCorrecta = false;
        this.mensajeError = 'La contraseña debe tener 6 caracteres';
      } else {
        this.ContactModel.passCorrecta = true;
        this.passWordCorrecta = true;
      }
    }

    /*METODO PARA VALIDAR LA CONTRASEÑA REPETIDA (AMBAS DEBEN COINCIDIR)*/
    public validatePassword(event: any): void {
      const repeatpass: string = event.target.value;
      this.ContactModel.repeatPassCorrecta = false;
      if (repeatpass != this.ContactModel.password) {
        this.ContactModel.repeatPassCorrecta = false;
        this.mensajePassword = 'La contraseña introducida no coincide';
        this.passWord2Correcta = false;
      } else {
        this.ContactModel.repeatPassCorrecta = true;
        this.passWord2Correcta = true;
      }
    }

    /*METODO COMPROBACION DE BOOLENAS DE CAMPOS OBLIGATORIOS PARA ACTIVAR EL BOTON DEL FORMULARIO*/
    public comprobarCamposObligatorios(camposCorrectos: boolean): boolean {
      if(this.ContactModel.password != this.ContactModel.repeatPassword) {
        this.passWord2Correcta = false;
        this.ContactModel.repeatPassCorrecta = false;
        camposCorrectos = false;
      } else if(this.nombreCorrecto == true &&
        this.apellidosCorrectos == true &&
        this.telefonoCorrecto == true &&
        this.mailCorrecto == true &&
        this.passWordCorrecta == true &&
        this.passWord2Correcta == true) {
          camposCorrectos = true;
      } else {
        camposCorrectos = false
      }
      return camposCorrectos;
    }

  }



<head>
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Poppins:wght@200;400;700&display=swap" rel="stylesheet">
</head>


<body>
    <h1 class="text-center">Registro</h1>
  <div class="container justify-content-center">

    <div id="flotante">
    <form (ngSubmit)="onSubmit(contactForm)" #contactForm="ngForm" class="justify-content-center" id="formulario"> <!--creamos nuestra variable template-->

      <div class="form-group row">
          <div class="col-12 col-md-6 bloque">
            <label for="nombre">*Nombre</label>
            <input type="text" class="form-control" name="nombre" #nombre [(ngModel)]="ContactModel.nombre" id="nombre" required  (keyup)="this.validarCamposObligatorios($event)">
            <div class="invalid-feedback">
              Introduzca nombre válido mínimo 3 caracteres
            </div>
          </div>

          <div class="col-12 col-md-6 bloque">
            <label for="apellidos">*Apellidos</label>  
            <input type="text" class="form-control" name="apellidos" #apellidos [(ngModel)]="ContactModel.apellidos" id="apellidos" required (keyup)="this.validarCamposObligatorios($event)">
            <div class="invalid-feedback">
              Introduzca apellidos válidos mínimo 3 caracteres
            </div>
          </div>
        </div>

      <div class="form-group bloque">
        <label for="telefono">*Número de teléfono</label>
        <input type="text" class="form-control" name="numeroTelefono" #numeroTelefono [(ngModel)]="ContactModel.numeroTelefono" id="telefono" placeholder="916162..." maxlength="9" (keyup)="this.validarCamposObligatorios($event)">
        <div class="invalid-feedback">
          El teléfono debe tener 9 dígitos
        </div>
      </div>

      <div class="form-group bloque">
        <label for="mail">*Correo electrónico</label>  
        <input type="email" class="form-control"  name="email" #email [(ngModel)]="ContactModel.email" id="email" aria-describedby="emailHelp" placeholder="yo@gmail.com" required (keyup)="this.validarCamposObligatorios($event)" >
        <div class="invalid-feedback">
          El correo electrónico debe contener "@"
        </div>
      </div>

      <div class="form-group bloque">
        <label for="direccion">Dirección</label>
        <input type="text" class="form-control" name="direccion" #direccion [(ngModel)]="ContactModel.direccion" id="direccion" placeholder="Calle / Avenida ...">
      </div>

      <div class="form-group row bloque">
      <div class="col-12 col-md-6">
        <label for="localidad">Localidad</label>
        <input type="text" class="form-control" name="localidad" #localidad [(ngModel)]="ContactModel.localidad" id="localidad">
      </div>

      <div class="col-12 col-md-6 bloque">
        <label for="provincia">Provincia</label>
        <select class="form-control custom-select my-1 mr-sm-2" name="provincia" #provincia [(ngModel)]="ContactModel.provincia" id="seleccionarProvincia">
          <option value="">Selecciona Provincia</option>
          <option value="Álava/Araba">Álava/Araba</option>
          <option value="Albacete">Albacete</option>
          <option value="Alicante">Alicante</option>
          <option value="Almería">Almería</option>
          <option value="Asturias">Asturias</option>
          <option value="Ávila">Ávila</option>
          <option value="Badajoz">Badajoz</option>
          <option value="Baleares">Baleares</option>
          <option value="Barcelona">Barcelona</option>
          <option value="Burgos">Burgos</option>
          <option value="Cáceres">Cáceres</option>
          <option value="Cádiz">Cádiz</option>
          <option value="Cantabria">Cantabria</option>
          <option value="Castellón">Castellón</option>
          <option value="Ceuta">Ceuta</option>
          <option value="Ciudad Real">Ciudad Real</option>
          <option value="Córdoba">Córdoba</option>
          <option value="Cuenca">Cuenca</option>
          <option value="Gerona/Girona">Gerona/Girona</option>
          <option value="Granada">Granada</option>
          <option value="Guadalajara">Guadalajara</option>
          <option value="Guipúzcoa/Gipuzkoa">Guipúzcoa/Gipuzkoa</option>
          <option value="Huelva">Huelva</option>
          <option value="Huesca">Huesca</option>
          <option value="Jaén">Jaén</option>
          <option value="La Coruña/A Coruña">La Coruña/A Coruña</option>
          <option value="La Rioja">La Rioja</option>
          <option value="Las Palmas">Las Palmas</option>
          <option value="León">León</option>
          <option value="Lérida/Lleida">Lérida/Lleida</option>
          <option value="Lugo">Lugo</option>
          <option value="Madrid">Madrid</option>
          <option value="Málaga">Málaga</option>
          <option value="Melilla">Melilla</option>
          <option value="Murcia">Murcia</option>
          <option value="Navarra">Navarra</option>
          <option value="Orense/Ourense">Orense/Ourense</option>
          <option value="Palencia">Palencia</option>
          <option value="Pontevedra">Pontevedra</option>
          <option value="Salamanca">Salamanca</option>
          <option value="Segovia">Segovia</option>
          <option value="Sevilla">Sevilla</option>
          <option value="Soria">Soria</option>
          <option value="Tarragona">Tarragona</option>
          <option value="Tenerife">Tenerife</option>
          <option value="Teruel">Teruel</option>
          <option value="Toledo">Toledo</option>
          <option value="Valencia">Valencia</option>
          <option value="Valladolid">Valladolid</option>
          <option value="Vizcaya/Bizkaia">Vizcaya/Bizkaia</option>
          <option value="Zamora">Zamora</option>
          <option value="Zaragoza">Zaragoza</option>
        </select>  
      </div>
    </div>

      <div class="form-group bloque">
        <label for="contraseña">*Constraseña</label>
        <input type="password" class="form-control" (keyup)="this.correctPass($event)" name="password" #password [(ngModel)]="ContactModel.password" id="password" required minlength="6" maxlength="6">
        <small class="form-text text-danger" *ngIf="!this.ContactModel.passCorrecta"> {{this.mensajeError}} </small>
      </div>

      <div class="form-group bloque">
          <label for="repetirContraseña">*Repetir constraseña</label>
          <input type="password" [disabled]="!this.ContactModel.passCorrecta" (keyup)="this.validatePassword($event)" class="form-control" name="repeatPassword" #repeatPassword [(ngModel)]="ContactModel.repeatPassword" id="repeatPassword" required minlength="6" maxlength="6">
          <small class="form-text text-danger" *ngIf="!this.ContactModel.repeatPassCorrecta"> {{this.mensajePassword}} </small>
        </div>

      <div class="form-check bloque">
        <input type="checkbox" class="form-check-input" name="condiciones" #condiciones [(ngModel)]="ContactModel.condiciones" id="checkbox">
        <label class="form-check-label" for="checkbox">Acepto las condiciones</label>
        <div>
          <small class="form-text text-muted" *ngIf="!this.ContactModel.condiciones">Es necesario aceptar los términos y condiciones</small>
        </div>
      </div>

      <!--EXTRA-->
      <div class="form-group bloque">
          <label for="comentarios">Comentarios</label>
          <textarea class="form-control" name="comentarios" #comentarios [(ngModel)]="ContactModel.comentarios" id="textArea" rows="3"></textarea>
      </div>

      <div>
        <small class="form-text text-muted">Los campos con asterisco * son obligatorios</small>
      </div>

      <div class="form-group bloque" id="botones">
        <button type="submit" [disabled]="!contactForm.form.valid || !this.ContactModel.condiciones || !this.comprobarCamposObligatorios(camposCorrectos)" class="btn btn-primary">Enviar</button>
      </div>

    </form>
  </div> <!--elemento flotante-->

  </div> <!--fin container-->

  </body>
