import { Component, ElementRef, HostListener, OnInit } from '@angular/core';
import { trigger, state, style, animate, transition } from '@angular/animations';


@Component({
  selector: 'app-about-us',
  templateUrl: './about-us.component.html',
  styleUrls: ['./about-us.component.css'],
  animations: [
    trigger('fade', [
      state('void', style({ opacity: 0 })),
      transition(':enter, :leave', [
        animate(1000)
      ])
    ]),
    trigger('slideUp', [
      state('void', style({ transform: 'translateY(100%)' })),
      transition(':enter', [
        animate(1000)
      ])
    ]),
    trigger('slideDown', [
      state('void', style({ transform: 'translateY(-100%)' })),
      transition(':enter', [
        animate(1000)
      ])
    ])
  ]
})
export class AboutUsComponent implements OnInit {


  public azulVisible: boolean = false;
  public rosaVisible: boolean = false;
  public amarilloVisible: boolean = false;
  public animado = false;

  constructor(private elementRef: ElementRef) { }

  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }


  // Función que se ejecuta al hacer scroll en la ventana
  @HostListener('window:scroll', ['$event'])
  checkScroll() {
    const azul = document.querySelector('#azul');
    const rosa = document.querySelector('#rosa');
    const amarillo = document.querySelector('#amarillo');

    if (azul && this.isElementInViewport(azul)) {
      azul.classList.add('animated', 'fadeInUp');
    }
    if (rosa && this.isElementInViewport(rosa)) {
      rosa.classList.add('animated', 'fadeInUp');
    }
    if (amarillo && this.isElementInViewport(amarillo)) {
      amarillo.classList.add('animated', 'fadeInUp');
    }
  }


  // Función que comprueba si un elemento está dentro del viewport
  isElementInViewport(el: any) {
    const rect = el.getBoundingClientRect();
    return (
      rect.top >= 0 &&
      rect.left >= 0 &&
      rect.bottom <= (window.innerHeight || document.documentElement.clientHeight) &&
      rect.right <= (window.innerWidth || document.documentElement.clientWidth)
    );
  }

}
